    <!-- Scroller div -->
    <script type="text/javascript">
    var opacity = 0;
    var incOpacity = 1;    
    var delayOpacity = 10;
    var i = 0;
    var iMax = $(".scroller").children().length;        
     
    function changeScroller() {
        opacity = 0;
        incOpacity = 1;

        $('.scroller').css("opacity", opacity);        

        $(".i-scroller").each(function( index ) {
            if(index == i) {                                
                $(this).css("display", "block");
            } else {                
                $(this).css("display", "none");
            }
        }); 

        i++;
        // cek if i = max
        if(i == iMax) {
            i = 0;
        }

        fadeIn();

        setTimeout(changeScroller, 15000);
    }

    function fadeIn() {        
        if(opacity < 1) {
            opacity = incOpacity / delayOpacity;        
            if(incOpacity <= delayOpacity) {
                $('.scroller').css("opacity", opacity); 
                setTimeout(fadeIn, 100);
                incOpacity++;          
            }
        }
    }

    changeScroller();

    </script>
